import { Action } from "../action"
import { userActionType } from "../action-types"

type userstate = {


    loadingUserCourse: boolean,

    successCourse: boolean,
    errorCourse: boolean,
    userCourses: []
}

const initialState : userstate = {

    loadingUserCourse: true,

    successCourse: false,
    errorCourse: false,
    userCourses: []
}

export const getUserAssignedCourseReducers = (_state: userstate = initialState, action : Action) => {

    switch (action.type) {


        case userActionType.ASSIGN_PROJECT_FAIL:
            return {

                ..._state,

                successAddCourse: false,

                errorAddCourse: true
            }
        break;

        case userActionType.ASSIGN_PROJECT_REQUEST:
            return {


                loadingUserCourse: true,

            }

        break;

        case userActionType.ASSIGN_PROJECT_SUCCESS:
            return {

                ..._state,
                loadingUserCourse: false,
                successCourse: true,
                userCourse 
            }


        break;

        default:
            return initialState



    }
}
