import * as React from 'react';
import { useState } from 'react';
import { Button, Container, Form, Nav, Navbar } from 'react-bootstrap';
import { useDispatch } from 'react-redux';
import { AssignProjectRequest, courseAddRequestAction, userinfoRequest } from '../state/action-creators';
import { courseAddReducer } from '../state/reducers/courseAddReducer';
import { useAppSelector } from '../Types';

import { alertset } from '../state/action-creators';
import Alerts from './Alertscreen';
import { Alertshow } from '../component/AlertShow';

import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { json } from 'stream/consumers';

import SignupScreen from './SignupScreen';
import ListAllUsersScreen from './ListAllUsersScreen';
import AddProjectScreen from './AddProjectScreen';
import AddUserScreen from './AddUserScreen';
interface IAppProps {
}

// Name        string `json:"name"`
// 	Assignee_id string `json:"assignee_id"`
// 	Status      string `json:"status"`

const NavigateScreen: React.FunctionComponent<IAppProps> = (props) => {

    const navigate = useNavigate()

    const {loadinguserinfo,successuserinfo,usersData} = useAppSelector(state => state.userinfoReducer)

    const [val,setVal] = useState(0)

        if(localStorage.getItem('role') == undefined)
        localStorage.setItem('role',usersData.role)


    const showusers = () => {

        setVal(1)
    }


    const addUser = () => {
        setVal(2)
    }

    const addProject = () => {
        setVal(3)
    }

    useEffect(() => {


        if(localStorage.getItem('userdata') == 'undefined')
        {
            navigate('/user/login')
        }
    },[])


    const dispatch = useDispatch()

    useEffect(() => {

        const datas : any = localStorage.getItem('userdata')

        const vals = JSON.parse(datas)

        const userid = vals.userId

        dispatch(userinfoRequest(userid))

    },[])

    console.log(val)

    return(
        <div style ={{marginTop : '70px'}}>
        {usersData.role == 'Admin'  || localStorage.getItem('role') == 'Admin' ? (<Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand >Welcome {localStorage.getItem('userdata').userName.userName} !!</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link><Button onClick = {showusers}>Show All Users</Button></Nav.Link>
            <Nav.Link ><Button onClick = {addUser}>Add User</Button></Nav.Link>
            <Nav.Link><Button onClick = {addProject}>Add Project</Button></Nav.Link>
            <Nav.Link><Button>Assigned Project</Button></Nav.Link>
          </Nav>
        </Container>
      </Navbar>) : (

<Navbar bg="dark" variant="dark">
<Container>
  <Navbar.Brand >{usersData.userName}</Navbar.Brand>
  <Nav className="me-auto">
    <Nav.Link><Button>Show All Users</Button></Nav.Link>
    <Nav.Link ><Button>Assigned Project</Button></Nav.Link>
  </Nav>
</Container>
</Navbar>
      )}

      <div>
         {val == 1 ? <ListAllUsersScreen/> : <div></div>}
         {val == 2 ? <AddUserScreen/> : <div></div>}
         {val == 3 ? <AddProjectScreen/> : <div></div>}
      </div>
        </div>
    );
};

export default NavigateScreen
